import React from 'react';

function Food({ name, picture }){
return (
  <div>
    <h2>I Like {name}!</h2>
    <img src={picture} />
  </div>
  );
}

const foodILike = [
  {
    id: 1,
    name: 'ramen',
    image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/dc/Shoyu_Ramen.jpg/1200px-Shoyu_Ramen.jpg',
  },
  { 
    id: 2,
    name: 'kimbap',
    image: 'https://recipe1.ezmember.co.kr/cache/recipe/2016/06/29/e7401296033ab8e4297cd53d71e1bba91.jpg',
  },
];


function App() {
  return (
    <div>
      {foodILike.map(dish => (
        <Food key={dish.id} name={dish.name} picture={dish.image} alt={name}/>
      ))}      
    </div>
  );
}

export default App;
